version.script:
import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.Insert;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;
import org.efaps.db.Instance;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Checking for import of JaasKeys for eFapsApp-Payroll");

SearchQuery query = new SearchQuery();
query.setQueryTypes("Admin_User_JAASKey");
query.addWhereExprEqValue("Key", "Payroll_Admin");
query.addSelect("OID");
query.execute();
if (!query.next()) {

    RunLevel.init("shell");
    RunLevel.execute();

    EFAPS_LOGGER.info("    Importing JaasKeys for eFapsApp-Payroll");
    URL url = new URL(EFAPS_ROOTURL, "Data/IMPORT_UserJAASKey.xml");
    DataImport imp = DataImport.readFile(url);
    imp.updateInDB();

    EFAPS_LOGGER.info("    Importing Date for eFapsApp-Payroll");
    URL url2 = new URL(EFAPS_ROOTURL, "Data/IMPORT_Currency.xml");
    DataImport imp2 = DataImport.readFile(url2);
    imp2.updateInDB();
}

UIUpdate update = new UIUpdate();

update.add2Menu("cf354312-fb84-4cf7-9d57-81a8426420e1", "87001cc3-c45c-44de-b8f1-776df507f268", 1);

update.add2Menu("e8cbbebe-2a81-4979-bcb3-e8cb6f6b3ecd", "136a5437-7642-4e09-8182-23fc97fedbab", 0);

update.add2Help("68acacc0-cf4f-4260-bf16-2a0dfb2edbe0", "dead549e-5cc6-49f9-9a79-8e33aa139f6d", -1);
